import React, { useState } from "react";
import {
  View,
  Text,
  Image,
  TouchableOpacity,
  FlatList,
  Dimensions
} from "react-native";
import { useMyContext } from "./MyContext";

const products = [
  { id: 1, name: "Cheezy Burguer", price: 10, uri: "https://static.vecteezy.com/system/resources/previews/022/911/698/large_2x/colorful-burger-icon-free-illustration-free-png.png" },
  { id: 2, name: "Produto 2", price: 15, uri: "https://i.pinimg.com/736x/d8/42/2e/d8422ea0ff820af4ce2df4bd24a91d58.jpg" },
  { id: 3, name: "Produto 3", price: 20, uri: "https://i.pinimg.com/736x/d8/42/2e/d8422ea0ff820af4ce2df4bd24a91d58.jpg" },
  { id: 4, name: "Produto 4", price: 25, uri: "https://i.pinimg.com/736x/d8/42/2e/d8422ea0ff820af4ce2df4bd24a91d58.jpg" },
  { id: 5, name: "Produto 5", price: 30, uri: "https://i.pinimg.com/736x/d8/42/2e/d8422ea0ff820af4ce2df4bd24a91d58.jpg" },
  { id: 6, name: "Produto 6", price: 35, uri: "https://i.pinimg.com/736x/d8/42/2e/d8422ea0ff820af4ce2df4bd24a91d58.jpg" },
  { id: 7, name: "Produto 7", price: 40, uri: "https://i.pinimg.com/736x/d8/42/2e/d8422ea0ff820af4ce2df4bd24a91d58.jpg" },
  { id: 8, name: "Produto 8", price: 45, uri: "https://i.pinimg.com/736x/d8/42/2e/d8422ea0ff820af4ce2df4bd24a91d58.jpg" },
  { id: 9, name: "Produto 9", price: 50, uri: "https://i.pinimg.com/736x/d8/42/2e/d8422ea0ff820af4ce2df4bd24a91d58.jpg" },
];

const HomeScreen = ({ navigation }) => {
  const { addToCart, delToCart, cart } = useMyContext();
  const [selectedQuantity, setSelectedQuantity] = useState(1);

  const renderProductsItem = ({ item }) => (
    <View style={{
      width: Dimensions.get('window').width / 2.4,
    }}>
      <TouchableOpacity
        key={item.id}
        style={{
          backgroundColor: item.id % 2 == 0 ? '#7f89c6' : '#a97ec5',
          alignItems: 'center',
          marginRight: 10,
          borderRadius: 16,
          borderTopEndRadius: 0,
          paddingVertical: 10
        }}
        onPress={() => {
          navigation.navigate("Product", { item: item });
        }}
      >
        <Image
          source={{
            uri: item.uri,
          }}
          style={{
            width: 100, height: 100,
            objectFit: 'fill',
            borderRadius: 16,
          }}
        />
        <Text style={{
          paddingVertical: 5,
          fontSize: 18,
          color: 'white',
          fontWeight: 500
        }}>{item.name}</Text>
        <Text style={{
          fontSize: 18,
          fontWeight: 500,
          color: 'white'
        }}>$ {item.price}</Text>
      </TouchableOpacity>
    </View>
  );

  const getLenCart = (cart) => {
    let total = 0;
    cart.map(item => total += item.quantity);
    return total
  }

  return (
    <View style={{
      flex: 1,
      justifyContent: "center",
      alignItems: "center",
      backgroundColor: '#7f89c6',
    }}>

      <View style={{ width: '100%', alignItems: 'center', paddingVertical: 10 }}>
        <Image
          source={{
            uri: 'https://pngfreepic.com/wp-content/uploads/2022/04/Delivery-courier-Motor-vehicle-Bike-Free-Vector-png.png',
          }}
          style={{
            width: '80%',
            aspectRatio: '1.3 / 1',
            objectFit: 'fill',
            borderRadius: 16,
            transform: [{ scaleX: -1 }]
          }}
        />
      </View>

      <View style={{
        width: '100%',
        backgroundColor: 'white',
        marginTop: 20,
        paddingVertical: 30,
        borderTopLeftRadius: 24,
        borderTopRightRadius: 24,
      }}>
        <View style={{
          width: '80%',
          marginHorizontal: '10%',
          flexDirection: 'row',
          justifyContent: 'space-between',
          alignItems: "center",
        }}>
          <TouchableOpacity style={{
            backgroundColor: '#7f89c6',
            padding: 10,
            borderRadius: 12
          }}
            disabled
            onPress={() => {
              addToCart({ ...item, quantity: selectedQuantity });
            }}
          >
            <Text style={{ color: 'white', fontWeight: 500 }}>Burguer</Text>
          </TouchableOpacity>

          <TouchableOpacity style={{ padding: 10 }}
            onPress={() => { }}
          >
            <Text style={{ color: 'black', fontWeight: 500 }}>Pizza</Text>
          </TouchableOpacity>

          <TouchableOpacity style={{ padding: 10 }}
            onPress={() => { }}
          >
            <Text style={{ color: 'black', fontWeight: 500 }}>Pasta</Text>
          </TouchableOpacity>

          <TouchableOpacity style={{ padding: 10 }}
            onPress={() => { }}
          >
            <Text style={{ color: 'black', fontWeight: 500 }}>Popular</Text>
          </TouchableOpacity>
        </View>
      </View>

      <FlatList
        data={products}
        numColumns={2}
        style={{
          width: '100%',
          backgroundColor: 'white',
        }}
        contentContainerStyle={{
          rowGap: 10,
          paddingBottom: 45,
          paddingHorizontal: 45,
        }}
        keyExtractor={(item) => item.id.toString()}
        renderItem={renderProductsItem}
      />

      <View style={{
        width: '100%',
        backgroundColor: 'white',
        paddingVertical: 30,
        borderTopWidth: 1,
        borderTopColor: 'darkgrey'
      }}>
        <View style={{
          width: '80%',
          marginHorizontal: '10%',
          flexDirection: 'row',
          justifyContent: 'space-between',
          alignItems: "center",
        }}>
          <TouchableOpacity style={{
            backgroundColor: '#E9E1EB',
            padding: 10,
            borderRadius: 12
          }}
            onPress={() => {
              navigation.navigate('Home')
            }}
          >
            <Image
              source={{
                uri: "https://cdn-icons-png.flaticon.com/512/25/25694.png",
              }}
              style={{ width: 20, height: 20 }}
            />
          </TouchableOpacity>

          <TouchableOpacity style={{ padding: 10 }}
            onPress={() => { }}
          >
            <Image
              source={{
                uri: "https://uxwing.com/wp-content/themes/uxwing/download/user-interface/search-icon.png",
              }}
              style={{ width: 20, height: 20 }}
            />
          </TouchableOpacity>

          <TouchableOpacity style={{ padding: 10 }}
            onPress={() => { }}
          >
            <Image
              source={{
                uri: "https://icons.veryicon.com/png/o/object/material-design-icons/notifications-1.png",
              }}
              style={{ width: 20, height: 20 }}
            />
          </TouchableOpacity>

          <TouchableOpacity style={{ padding: 10 }}
            onPress={() => {
              navigation.navigate('Cart')
            }}
          >
            <Image
              source={{
                uri: "https://cdn-icons-png.flaticon.com/512/7605/7605078.png",
              }}
              style={{ width: 20, height: 20 }}
            />
          </TouchableOpacity>
        </View>
      </View>
    </View>
  );
};
export default HomeScreen;
